mdq:check.code:
library(stringr)

if (length(abstract) == 0) {
  status <- "FAILURE"
  message <- "An abstract is not present."
} else if (length(abstract) > 1) {
  status <- "FAILURE"
  message <- "More than one abstract is present. Only one is allowed."
} else {
  # Trim whitespace, split abstract on whitespace
  tokens <- trimws(str_split(abstract, "\\s+")[[1]], which = "both")
  # Remove blank elements (subtly and irritatingly different than whitespace)
  tokens <- tokens[tokens != ""]

  if (length(tokens) >= 100) {
    status <- "SUCCESS"
    message <- sprintf("The abstract is %d words long, which is sufficient.", length(tokens))
  } else {
    status <- "FAILURE"
    message <- sprintf("The abstract is only %d words long. 100 or more words are required.", length(tokens))
  }
}

mdq_result <- list(status = status,
                   output = list(list(value = message)))
