mdq:check.code:
check <- function() {
  library(base)
  # These variables are defined by MQE from the <xpath> selectors.
  # "datasetTitle" - the value of the <title> of the dataset
  minWordCount <- 7
  maxWordCount <- 20
  if(!exists("datasetTitle") || is.null(datasetTitle)) {
    return(list(status = "FAILURE", output = sprintf("The dataset title is not present, so the check is unable to determine title word length.")))
  }

  wordCount <- length(unlist(strsplit(datasetTitle, "\\s+", perl=T)))
  if (wordCount < minWordCount) {
    return(list(status = "FAILURE", output = sprintf("The dataset title word count is %d. The mininum recommended word count is %s", wordCount, minWordCount))) 
  } else if (wordCount > maxWordCount) {
    return(list(status = "FAILURE", output = sprintf("The dataset title word count is %d. The maximum recommended word count is %s", wordCount, maxWordCount))) 
  } else {
    return(list(status = "SUCCESS", output = sprintf("The dataset title word count is between %d and %d characters.", minWordCount, maxWordCount))) 
  }
}
result <- check()
mdq_result <- list(status=result$status, output=list(list(value=result$output)))
