mdq:check.code:
check <- function() {
  if (!titlePresent) {
    return(list(status = "FAILURE",
                output = "A title is not present, so unable to check title word length."))
  }

  # Required minimum word count
  strictMinCount <- 5
  # Recommended minimum word count
  minWordCount <- 7
  # Recommended maximum word count
  maxWordCount <- 20

  wordCount <- length(unlist(strsplit(datasetTitle, "\\s+", perl = TRUE)))
  if (wordCount < strictMinCount) {
    return(list(status = "FAILURE",
                output = sprintf("The title is %d words long. The minimum required word count is %d.", wordCount, strictMinCount)))
  } else if (wordCount < minWordCount) {
    return(list(status = "FAILURE",
                output = sprintf("The title is %d words long. The minimum recommended word count is %d.", wordCount, minWordCount)))
  } else if (wordCount > maxWordCount) {
    return(list(status = "FAILURE",
                output = sprintf("The title is %d words long. The maximum recommended word count is %d.", wordCount, maxWordCount)))
  } else {
    return(list(status = "SUCCESS",
                output = sprintf("The title is %d words long, which is sufficient.", wordCount)))
  }
}

result <- check()

mdq_result <- list(status = result$status,
                   output = list(list(value = result$output)))
