mdq:check.code:
if (is.null(awards)) {
  status <- "SUCCESS"
  output <- "NA"
  mdq_result <- list(status = status,
                     output = list(list(value = output)))
} else {
  status <- "SUCCESS"
  mdq_result <- list(status = status,
                     output = lapply(awards, function(message) { list(value = message)}))

}
