mdq:check.code:
check <- function() {
    # Check that a <methods> element is present for the dataset.
    if(methodsPresent) {
        status <- "SUCCESS"
        output <- "A methods element is present"
    } else {
        status <- "FAILURE"
        output <- "A methods element is not present"
    }
    mdq_result <- list(status=status, output=list(list(value=output)))
    return (mdq_result)
}

mdq_result <- check()
