mdq:check.code:
if (is.null(awards)) {
  status <- "FAILURE"
  output <- "No award numbers are present."
} else if (length(awards) < 1) {
  status <- "FAILURE"
  output <- "No award numbers are present when one or more are expected."
} else if (all(nchar(awards) <= 0)) {
  status <- "FAILURE"
  output <- "Award numbers appear to be present but are zero in length."
} else {
  status <- "SUCCESS"
  output <- "At least one award number is present."
}

mdq_result <- list(status = status,
                   output = list(list(value = output)))
