mdq:check.code:
library(metadig)
library(lubridate)

if (length(pub_date) <= 0) {
  failure("A publication date is not present.")
} else {
  if (nchar(pub_date) == 4 && is.na((as.numeric(pub_date)))) {
    failure("A publication date is present but should be in YYYY or YYYY-MM-DD format.")
  } else if (nchar(pub_date) == 4 && !is.na(as.numeric(pub_date))) {
    success("A publication date is present with YYYY format.")
  } else if (nchar(pub_date) == 10 && is.na(ymd(pub_date))) {
    failure("A publication date is present but should be in YYYY or YYYY-MM-DD format.")
  } else if (nchar(pub_date) == 10 && !is.na(ymd(pub_date))) {
    success("A publication date is present with YYYY-MM-DD format.")
  } else {
    failure("A publication date is present but should be in YYYY or YYYY-MM-DD format.")
  }
}
