mdq:check.code:
check <- function() {
  # These variables are defined by the MQE from the <xpath> selectors.
  # "extendDescriptionPresent" - is a methods sampling description present?

  if (extentDescriptionPresent) {
    return(list(status = "SUCCESS",
                output = "A sampling study extent description is present in the methods section."))
  } else {
    return(list(status = "FAILURE",
                output = "A sampling study extent description is not present in the methods section."))
  }
}

result <- check()

mdq_result <- list(status = result$status,
                   output = list(list(value = result$output)))
